const Cart = () => {
  const cart = useSelector((state) => state.cart);
  const auth = useSelector((state) => state.auth);

  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(()=>{
    dispatch(getTotals());
  }, [cart, dispatch]);

  const handleAddToCart = (product) => {
    dispatch(addToCart(product));
  }

  const handleDecreaseCart = (product) => {
    dispatch(decreaseCart(product));
  }

  const handleremoveFromCart = (product) => {
    dispatch(removeFromCart(product));
  }

  const handleClearCart = (product) => {
    dispatch(clearCart())
  }

    return (
    <Container>
      <Navbar />
      <Wrapper>
        <Title>Таний сагс</Title>
        {cart.cartItems.length === 0 ? (
        <div className="cart-empty">
          <p>Your cart is currently empty</p>
          <div className="start-shopping">
            <Link to="/">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="20"
                height="20"
                fill="currentColor"
                className="bi bi-arrow-left"
                viewBox="0 0 16 16"
              >
                <path
                  fillRule="evenodd"
                  d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"
                />
              </svg>
              <span>Start Shopping</span>
            </Link>
          </div>
        </div>
      ) : (
        <Bottom>
          <Info>
            {cart.cartItems.map((cartItem) => (
              <Product key={cartItem._id}>
                <ProductDetail>
                  <Image src={cartItem.img}/>
                  <Details>
                    <ProductName>
                      <b>Бүтээгдэхүүн:</b> {cartItem.title}
                    </ProductName>
                    <ProductSize>
                      <b>Диаметр:</b> {cartItem.size}
                    </ProductSize>
                    <ProductLength>
                      <b>Хэмжээ</b> {cartItem.length}
                    </ProductLength>
                  </Details>
                </ProductDetail>
                <PriceDetail>
                <ProductAmountContainer>
                Тоо хэмжээ
                  <Button onClick={() => handleDecreaseCart(cartItem)}>  -  </Button>
                      <ProductAmount> {cartItem.cartQuantity}</ProductAmount>
                  <Button onClick={() => handleAddToCart(cartItem)}>+</Button>
                </ProductAmountContainer>
                    <Button  onClick={() => handleremoveFromCart(cartItem)}>
                        Устгах
                    </Button>
                  <ProductPrice>
                    {cartItem.price * cartItem.cartQuantity}₮
                  </ProductPrice>
                </PriceDetail>
              </Product>
            ))}
            <Hr />
          </Info>
          <Summary>
            <SummaryFixed>
            <SummaryTitle>Захилга өгөх</SummaryTitle>
            <SummaryItem type="total">
              <SummaryItemText>НИЙТ ДҮН  :</SummaryItemText>
              <SummaryItemPrice><span>{cart.cartTotalAmount}</span> ₮</SummaryItemPrice>
            </SummaryItem>
            {auth._id ? (
              <PayButton cartItems={cart.cartItems} />
            ) : (
              <Button 
              onClick={() => navigate("/login")}>
                Нэвтэрч орно уу!
              </Button>
            )
            }
              <Button onClick={() => handleClearCart()}> Сагс хоослох </Button>
            <div className="continue-shopping">
                <Link to="/">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="20"
                    height="20"
                    fill="currentColor"
                    className="bi bi-arrow-left"
                    viewBox="0 0 16 16"
                    >
                    <path
                      fillRule="evenodd"
                      d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"
                      />
                  </svg>
                  <span>Нүүр хуудасруу буцах</span>
                </Link>
              </div>
                      </SummaryFixed>
          </Summary>
        </Bottom>
        )}
      </Wrapper>
      <Footer />
    </Container>
  );
};

