import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import { clearCart, getTotals } from "../slices/cartSlice";
const Success = () => {

  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart);

  useEffect(() => {
    dispatch(clearCart());
  }, [dispatch]);

  useEffect(() => {
    dispatch(getTotals());
  }, [cart, dispatch]);


  return (

    <div
      style={{
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {orderId
        ? `Tanii zahialgiin dugaar ${orderId}`
        : `Захилга амжилттай`}
        <Link to="/">
      <button onClick={() => dispatch(clearCart())} style={{ padding: 10, marginTop: 20 }}>Нүүр хуудасруу буцах</button>
        </Link>
    </div>
  );
};

export default Success;
